        <ul class=\"navbar-nav mr-auto \">
            <li class=\"nav-item \">
                <a class=\"nav-link\" href=\"{{ path('product_index') }}\">{% trans %}Accueil{% endtrans %}<span class=\"sr-only\">(current)</span></a>
            </li>
        </ul>
        <a href=\"{{ path('cart_show') }}\" class=\"btn btn-outline-info my-2 my-sm-0\" type=\"submit\">
