        <span class=\"ml-2 text-info\"><a href=\"{{ path('locale', {locale: 'fr'}) }}\" class=\"text-info\">fr</a> | <a href=\"{{ path('locale', {locale: 'en'}) }}\" class=\"text-info\">en</a></span>
